import React  from 'react'

function Form({data,setData,setInput}){
  //prevents page from reloading everytime we click the register button.

  const handleChange=(e)=> {
    setData({
      ...data,
      [e.target.name]: e.target.value

    })



  }
  console.log(setData)
const handleFormSubmit=(e)=>{
  e.preventDefault()
}

  return (
    <div className='container'>
     <form>
           <h3>Registration</h3>
           <div className='inputs' >
          <label className="label-form" htmlFor='first-name'>First Name</label>
          <input type="text" name='first_name'onChange={handleChange} required/>
          <label className="label-form" htmlFor='last-name'>Last Name</label>
          <input type="text" name='last_name' required/>
          <label className="label-form" htmlFor='email'>Email</label>
          <input type="email" name='email' onChange={handleChange}/>
          <label className="label-form" htmlFor='password'> Password</label>
          <input type="password" name='password' onChange={handleChange} required/>
          <label className="label-form" htmlFor='confirm-password'> Password</label>
          <input type="password" name='confirm_password' onChange={handleChange} required/>
          </div>
          <button className='button' onClick={handleFormSubmit}>Register</button>
          </form>


    </div>
  )
}
export default Form;
