import React  from 'react'

function Form({data,setData,setInput}){
  //prevents page from reloading everytime we click the register button.
const handleFormSubmit=(e)=>{
  e.preventDefault()
}
const handleChange=(e)=> {
  setData({
    ...data,
    [e.target.name]: e.target.value
  })
  setInput('')
}
  return (
    <div className='container'>
     <form>
           <h3>Registration</h3>
           <div className='inputs' >
          <label for="First-name">First Name</label>
          <input type="text" name='first_name'onChange={handleChange} required/>
          <label for="Last-Name">Last Name</label>
          <input type="text" name='last_name' required/>
          <label for="Email">Email</label>
          <input type="email" name='email' onChange={handleChange}/>
          <label for="Password"> Password</label>
          <input type="password" name='password' onChange={handleChange} required/>
          <label for="confirm-password"> Password</label>
          <input type="password" name='confirm_password' onChange={handleChange} required/>
          </div>
          <button className='button' onClick={handleFormSubmit}>Register</button>
          </form>


    </div>
  )
}
export default Form;
