import React,{useState} from 'react'

function SignUp(){
  // 3 states to get the Registration input field values 
  const[name,setName]=useState("")
  const [password,setPassword]=useState("")
  const [email,setEmail]=useState("")

 function register(){
  // console.log(name,password,email) prints the values of inputs in console
  // convert these 3 statement into an object
   let item={name,password,email}
   console.log(item) 
 }

 function handleSubmit(e){
  e.preventDefault() 
  // const url=""
  // const [data,setData]=useState({
  //   name: "",
  //   email: "",
  //   password: ""
  // })
 }
  return (
    <div className='Register'>
      <form onSubmit={handleSubmit}>  
          <div className='inputs' >
          <label className="label-form" htmlFor='name'>Name</label>
          <input type="text" value={name} onChange={(e)=>setName(e.target.value)} required/>
          <label className="label-form" htmlFor='email'>Email</label>
          <input type="email" value={email} onChange={(e)=>setEmail(e.target.value)} required/>
          <label className="label-form" htmlFor='password'> Password</label>
          <input type="password" value={password} onChange={(e)=>setPassword(e.target.value)}required/>
          <button onClick={register}>Register</button>
          </div> 
      </form>

   </div>
  )
}
export default SignUp;
