import React, { useState }  from 'react'

function Form(){
  //prevents page from reloading everytime we click the register button.
  const[data,setData]=useState('')
  const [input,setInput]=useState({
    first_name: "",
    last_name: "",
    email: "",
    password: "",
    confirm_password: "",
  })
  const handleSubmit=(e)=>{
    e.preventDefault()
  }

  console.log(input)

  const handleChange=(e)=> {
    setInput({
      ...input,
      [e.target.name]: e.target.value

    })



  }

const handleFormSubmit=(e)=>{
  e.preventDefault()
}

  return (
    <div className='container'>
     <form onSubmit={handleSubmit}>
           <h3>Registration</h3>
           <div className='inputs' >
          <label className="label-form" htmlFor='first-name'>First Name</label>
          <input type="text" name='first_name' value={input.first_Name}onChange={handleChange} required/>
          <label className="label-form" htmlFor='last-name'>Last Name</label>
          <input type="text" name='last_name' value={input.second_name}required/>
          <label className="label-form" htmlFor='email'>Email</label>
          <input type="email" name='email'value={input.email} onChange={handleChange}/>
          <label className="label-form" htmlFor='password'> Password</label>
          <input type="password" name='password' value={input.password} onChange={handleChange} required/>
          <label className="label-form" htmlFor='confirm-password'> Password</label>
          <input type="password" name='confirm_password'value={input.confirm_password} onChange={handleChange} required/>
          </div>
          <button className='button' type='onSubmit'>Register</button>
          </form>


    </div>
  )
}
export default Form;
