import React,{useState} from 'react'

function Form(){
  // 3 states to get the Registration input field values 
  const[name,setName]=useState("")
  const [password,setPassword]=useState("")
  const [email,setEmail]=useState("")
  // // 2 states to get the Login input field values
  const [yourEmail,setYourEmail]=useState("")
  const[yourPassword,setYourPassword]=useState("")
 function register(){
  // console.log(name,password,email) prints the values of inputs in console
  // convert these 3 statement into an object
  let item={name,password,email}
  // console.log(item) prints the object in conolse
 }

 function handleSubmit(e){
  e.preventDefault() 
 }
  return (
    <div className='container'>
      <div className='Register'>
     <form onSubmit={handleSubmit}>
           <h3>Registration</h3>
           <div className='inputs' >
          <label className="label-form" htmlFor='name'>Name</label>
          <input type="text" value={name} onChange={(e)=>setName(e.target.value)}/>
          <label className="label-form" htmlFor='email'>Email</label>
          <input type="email" value={email} onChange={(e)=>setEmail(e.target.value)}/>
          <label className="label-form" htmlFor='password'> Password</label>
          <input type="password" value={password} onChange={(e)=>setPassword(e.target.value)}/>
          </div>
          <button onClick={register}>Register</button>
          </form>
         </div>  

      <div className='login'>
        <h3>Login</h3>
        <label className='label-form' htmlFor="email">Email</label>
        <input type="email" onChange={()=>setYourEmail()}/>
        <label className='label-form' htmlFor="email">Password</label>
        <input type="password" onChange={()=>setYourPassword()}/>



        </div> 

    </div>
  )
}
export default Form;
